catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var form = g_form.getControl('description');
    var description = form.value.toLowerCase(); // Convert to lowercase for case-insensitive search
    var keywordPattern = /\b(independent eval|principal investigator|grant|referral|bonus|relocation|spot|sign|signon|sign-on|severance|separation)\b/i; // Define regular expression used to find the keywords (whole words, not letter patterns)
    var keywordFound = keywordPattern.test(description); // Scan the description text for keywords

    // If keywords are found, then keywordFound is True, which shows the warning text and checkbox field and makes the checkbox mandatory to prevent form submission.
    // If no keywords are found, then keywordFound is False, and everything stays hidden and not-mandatory and life goes happily on.
    g_form.setVisible('formatter', keywordFound);
	g_form.setVisible('warning_text', keywordFound);
    g_form.setMandatory('validation_check', keywordFound);
    g_form.setVisible('validation_check', keywordFound);
}
