sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var userID = gs.getUserID();
    var userRec = new GlideRecord('sys_user');
    userRec.get(userID);
    var isManager = userRec.getValue("u_is_manager");
    //limits records in query to just those requested for you or opened by you if you do not have a role below or are not a manager
    if ((!gs.hasRole("x_shce_wage_adjust.approver") && !gs.hasRole("x_shce_wage_adjust.payor")) || //Not approver or payor
        gs.isInteractive() && //uses the GUI
        (current.employee_name == userID) || //you're the employee
		(current.manager_to_approve == userID) || //or you're the approver
		(current.waf_creator == userID) //or you created it
    ) {
        var u = gs.getUserID();
        current.addQuery("employee_name", u).addOrCondition("manager_to_approve", u).addOrCondition("waf_creator", u);
    }
})(current, previous);
